function copyTextToClipboard(btnElem, data) {
    if (document.queryCommandSupported && document.queryCommandSupported("copy")) {
        var textarea = document.createElement("textarea"); //NO I18N
        textarea.textContent = unescape(data);
        document.body.appendChild(textarea);
        textarea.select();
        try {
            document.execCommand("copy"); // Security exception may be thrown by some browsers. //NO I18N
        } catch (ex) {}
        document.body.removeChild(textarea);
        $(btnElem).find('.codeCopyBtnTooltip').addClass('codeCopyBtnTooltipActive');
        setTimeout(function(){ $('.codeCopyBtnTooltipActive').removeClass('codeCopyBtnTooltipActive'); }, 700);
    }
}
